import React, { useEffect, useRef } from 'react';

const EmberEffect = () => {
  const containerRef = useRef(null);
  
  useEffect(() => {
    const container = containerRef.current;
    const maxEmbers = 30;
    const embersArray = [];
    
    // Função para criar uma partícula de brasa
    function createEmber() {
      const ember = document.createElement('div');
      ember.classList.add('ember-particle');
      
      // Tamanho aleatório
      const size = Math.random() * 5 + 2;
      ember.style.width = `${size}px`;
      ember.style.height = `${size}px`;
      
      // Posição inicial aleatória em X
      const posX = Math.random() * window.innerWidth;
      ember.style.left = `${posX}px`;
      
      // Posição inicial na parte inferior
      const posY = window.innerHeight + 10;
      ember.style.bottom = `-${posY}px`;
      
      // Direção e velocidade aleatórias
      const direction = Math.random() > 0.5 ? 1 : -1;
      ember.style.setProperty('--direction', direction);
      
      // Duração da animação
      const duration = Math.random() * 6 + 8;
      ember.style.animationDuration = `${duration}s`;
      
      // Atraso inicial para não iniciar tudo ao mesmo tempo
      const delay = Math.random() * 10;
      ember.style.animationDelay = `${delay}s`;
      
      // Cor variável (vermelho/laranja/amarelo)
      const hue = Math.floor(Math.random() * 30) + 20; // 20-50 (vermelho para laranja)
      const saturation = Math.floor(Math.random() * 20) + 80; // 80-100%
      const lightness = Math.floor(Math.random() * 20) + 50; // 50-70%
      
      ember.style.backgroundColor = `hsl(${hue}, ${saturation}%, ${lightness}%)`;
      ember.style.boxShadow = `0 0 ${size * 2}px hsl(${hue}, ${saturation}%, ${lightness}%)`;
      
      container.appendChild(ember);
      
      // Adicionar ao array para controle
      embersArray.push({
        element: ember,
        remove: false,
      });
      
      // Remover após a animação terminar
      setTimeout(() => {
        const index = embersArray.findIndex(item => item.element === ember);
        if (index !== -1) {
          embersArray[index].remove = true;
          ember.remove();
          embersArray.splice(index, 1);
        }
      }, (duration + delay) * 1000);
    }
    
    // Inicialmente criar algumas partículas
    for (let i = 0; i < maxEmbers / 2; i++) {
      createEmber();
    }
    
    // Intervalo para adicionar novas partículas
    const emberInterval = setInterval(() => {
      if (embersArray.length < maxEmbers) {
        createEmber();
      }
    }, 1000);
    
    // Limpar ao desmontar o componente
    return () => {
      clearInterval(emberInterval);
      embersArray.forEach(ember => {
        if (ember.element) {
          ember.element.remove();
        }
      });
    };
  }, []);
  
  return (
    <div 
      ref={containerRef} 
      className="fixed inset-0 pointer-events-none overflow-hidden"
      aria-hidden="true"
    />
  );
};

export default EmberEffect;
