import React, { useState } from 'react';
import { motion } from 'framer-motion';

const FAQItem = ({ question, answer }) => {
  const [isOpen, setIsOpen] = useState(false);
  
  return (
    <div className="border-b border-amber-700/30 pb-4 mb-4">
      <button 
        className="flex justify-between items-center w-full text-left focus:outline-none"
        onClick={() => setIsOpen(!isOpen)}
      >
        <h3 className="text-lg font-bold text-white">{question}</h3>
        <svg 
          className={`w-5 h-5 text-amber-400 transition-transform ${isOpen ? 'transform rotate-180' : ''}`} 
          fill="none" 
          stroke="currentColor" 
          viewBox="0 0 24 24"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
        </svg>
      </button>
      
      {isOpen && (
        <motion.div
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: "auto" }}
          transition={{ duration: 0.3 }}
          className="mt-4 text-slate-300"
        >
          <p>{answer}</p>
        </motion.div>
      )}
    </div>
  );
};

const SupportPage = () => {
  // Estado para o formulário de contato
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: 'Dúvida sobre o jogo',
    message: ''
  });
  
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    // Simulação de envio de formulário
    setTimeout(() => {
      setIsSubmitting(false);
      setSubmitSuccess(true);
      // Resetar formulário após 3 segundos
      setTimeout(() => {
        setSubmitSuccess(false);
        setFormData({
          name: '',
          email: '',
          subject: 'Dúvida sobre o jogo',
          message: ''
        });
      }, 3000);
    }, 1500);
  };
  
  // Dados do FAQ
  const faqItems = [
    {
      question: "Quando Lendas de Arcadia será lançado?",
      answer: "Estamos trabalhando ativamente no desenvolvimento e planejamos uma versão 1.0 para o primeiro trimestre de 2024. Enquanto isso, acompanhe nosso roadmap para ver nosso progresso."
    },
    {
      question: "O jogo será gratuito?",
      answer: "Sim! O jogo base será gratuito para jogar. No futuro, poderemos oferecer DLCs e conteúdos cosméticos opcionais que suportarão o desenvolvimento contínuo do jogo."
    },
    {
      question: "Em quais plataformas o jogo estará disponível?",
      answer: "Inicialmente o lançamento será para Windows PC via nossa página web e possivelmente Steam. Planejamos portar para macOS e Linux após o lançamento inicial."
    },
    {
      question: "Como posso reportar bugs ou sugerir novas funcionalidades?",
      answer: "Você pode reportar bugs ou fazer sugestões através do nosso repositório GitHub, no canal de Discord ou usando o formulário de contato nesta página."
    },
    {
      question: "O jogo terá modo multiplayer?",
      answer: "O modo multiplayer cooperativo está planejado para ser adicionado após o lançamento da versão 1.0. Está em nossa lista de prioridades para expansões futuras."
    },
    {
      question: "O jogo terá suporte para outros idiomas?",
      answer: "Sim! O jogo será lançado em Português (Brasil) e Inglês inicialmente, com planos para adicionar Espanhol, Francês e outros idiomas no futuro."
    }
  ];
  
  return (
    <div className="py-6">
      <div className="container mx-auto px-4">
        <motion.h1
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="text-4xl font-medieval text-amber-400 mb-8 text-center"
        >
          Suporte e Comunidade
        </motion.h1>
        
        {/* Seção de Suporte */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 mb-16">
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
            className="bg-slate-800/50 border border-amber-700/30 rounded-lg p-6 shadow-xl"
          >
            <h2 className="text-2xl font-medieval text-amber-400 mb-6">Entre em Contato</h2>
            
            {!submitSuccess ? (
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label htmlFor="name" className="block text-sm font-medium text-slate-300 mb-1">
                    Nome
                  </label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                    className="w-full px-4 py-2 rounded bg-slate-700/50 border border-slate-600 text-white focus:outline-none focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  />
                </div>
                
                <div>
                  <label htmlFor="email" className="block text-sm font-medium text-slate-300 mb-1">
                    Email
                  </label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    className="w-full px-4 py-2 rounded bg-slate-700/50 border border-slate-600 text-white focus:outline-none focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  />
                </div>
                
                <div>
                  <label htmlFor="subject" className="block text-sm font-medium text-slate-300 mb-1">
                    Assunto
                  </label>
                  <select
                    id="subject"
                    name="subject"
                    value={formData.subject}
                    onChange={handleChange}
                    className="w-full px-4 py-2 rounded bg-slate-700/50 border border-slate-600 text-white focus:outline-none focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  >
                    <option value="Dúvida sobre o jogo">Dúvida sobre o jogo</option>
                    <option value="Reporte de bug">Reporte de bug</option>
                    <option value="Sugestão">Sugestão</option>
                    <option value="Parceria">Parceria</option>
                    <option value="Outro">Outro</option>
                  </select>
                </div>
                
                <div>
                  <label htmlFor="message" className="block text-sm font-medium text-slate-300 mb-1">
                    Mensagem
                  </label>
                  <textarea
                    id="message"
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    required
                    rows="5"
                    className="w-full px-4 py-2 rounded bg-slate-700/50 border border-slate-600 text-white focus:outline-none focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  ></textarea>
                </div>
                
                <div>
                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className={`px-6 py-3 rounded bg-amber-600 hover:bg-amber-700 text-white font-bold transition w-full flex justify-center items-center ${isSubmitting ? 'opacity-70 cursor-not-allowed' : ''}`}
                  >
                    {isSubmitting ? (
                      <>
                        <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                        </svg>
                        Enviando...
                      </>
                    ) : (
                      'Enviar Mensagem'
                    )}
                  </button>
                </div>
              </form>
            ) : (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="bg-green-900/40 border border-green-700 rounded-lg p-6 text-center"
              >
                <svg className="w-16 h-16 text-green-400 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                </svg>
                <h3 className="text-xl font-bold text-white mb-2">Mensagem Enviada!</h3>
                <p className="text-green-300">Obrigado por entrar em contato. Responderemos o mais breve possível.</p>
              </motion.div>
            )}
          </motion.div>
          
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: 0.3 }}
          >
            <div className="bg-slate-800/50 border border-amber-700/30 rounded-lg p-6 shadow-xl mb-6">
              <h2 className="text-2xl font-medieval text-amber-400 mb-4">Canais de Comunicação</h2>
              <ul className="space-y-4">
                <li className="flex items-center">
                  <div className="w-10 h-10 flex items-center justify-center bg-violet-800/50 rounded-full mr-4">
                    <svg className="w-5 h-5 text-violet-300" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M20.317 4.3698a19.7913 19.7913 0 00-4.8851-1.5152.0741.0741 0 00-.0785.0371c-.211.3753-.4447.8648-.6083 1.2495-1.8447-.2762-3.68-.2762-5.4868 0-.1636-.3847-.4058-.874-.6177-1.2495a.077.077 0 00-.0785-.037 19.7363 19.7363 0 00-4.8852 1.515.0699.0699 0 00-.0321.0277C.5334 9.0458-.319 13.5799.0992 18.0578a.0824.0824 0 00.0312.0561c2.0528 1.5076 4.0413 2.4228 5.9929 3.0294a.0777.0777 0 00.0842-.0276c.4616-.6304.8731-1.2952 1.226-1.9942a.076.076 0 00-.0416-.1057c-.6528-.2476-1.2743-.5495-1.8722-.8923a.077.077 0 01-.0076-.1277c.1258-.0943.2517-.1923.3718-.2914a.0743.0743 0 01.0776-.0105c3.9278 1.7933 8.18 1.7933 12.0614 0a.0739.0739 0 01.0785.0095c.1202.099.246.1981.3728.2924a.077.077 0 01-.0066.1276 12.2986 12.2986 0 01-1.873.8914.0766.0766 0 00-.0407.1067c.3604.698.7719 1.3628 1.225 1.9932a.076.076 0 00.0842.0286c1.961-.6067 3.9495-1.5219 6.0023-3.0294a.077.077 0 00.0313-.0552c.5004-5.177-.8382-9.6739-3.5485-13.6604a.061.061 0 00-.0312-.0286zM8.02 15.3312c-1.1825 0-2.1569-1.0857-2.1569-2.419 0-1.3332.9555-2.4189 2.157-2.4189 1.2108 0 2.1757 1.0952 2.1568 2.419 0 1.3332-.9555 2.4189-2.1569 2.4189zm7.9748 0c-1.1825 0-2.1569-1.0857-2.1569-2.419 0-1.3332.9554-2.4189 2.1569-2.4189 1.2108 0 2.1757 1.0952 2.1568 2.419 0 1.3332-.946 2.4189-2.1568 2.4189Z" />
                    </svg>
                  </div>
                  <div>
                    <h3 className="font-bold text-white">Discord</h3>
                    <a href="https://discord.gg/lendasarcadia" className="text-blue-400 hover:underline">discord.gg/lendasarcadia</a>
                  </div>
                </li>
                
                <li className="flex items-center">
                  <div className="w-10 h-10 flex items-center justify-center bg-blue-800/50 rounded-full mr-4">
                    <svg className="w-5 h-5 text-blue-300" fill="currentColor" viewBox="0 0 24 24">
